sysauto_script.script:
//Gebe uns alle Usertimecards wieder
var userTimecardGR = new GlideRecord('x_42072_expense_ma_usertimecard');
userTimecardGR.query();


var mon = new GlideDate(); //Start der Woche
var sun = new GlideDate(); //Ende der Woche
sun.addDaysLocalTime(6);

while(userTimecardGR.next()){
	//SYSID DER USERCARD
  var utcSysID = userTimecardGR.sys_id;
//Weekly Timecard Initialization
 var weeklyTimecardGR = new GlideRecord('x_42072_expense_ma_weekly_timecard');
    weeklyTimecardGR.initialize();
    weeklyTimecardGR.setValue('user_timecard', utcSysID); 
    weeklyTimecardGR.setValue('start_of_week', mon); // Montag 
    weeklyTimecardGR.setValue('end_of_week', sun); // Sonntag 
    weeklyTimecardGR.setValue('required_hours', 40); //  Stunden
    weeklyTimecardGR.insert();
}

gs.log("WÃ¶chentliche Timecards wurden erstellt.");
